window.onscroll = function() {
  if (navbar) {
	  myFunction();
	} else {
	  console.clear();
	  console.log("The ID, hasn't been found..");
	}
}

var navbar = document.getElementById("use-navbar-sticky");
var sticky = navbar.offsetTop;

function myFunction() {
  if (window.pageYOffset >= sticky) {
    navbar.classList.add("use-sticky")
  } else {
    navbar.classList.remove("use-sticky");
  }
}